class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        count = 0
        freq = {'a': 0, 'b': 0, 'c': 0}
        left = 0
        length = len(s)
        for right, ch in enumerate(s):
            freq[ch] += 1
            while freq['a'] and freq['b'] and freq['c']:
                count += length - right
                freq[s[left]] -= 1
                left += 1
        return count

            


if __name__ == '__main__':
    solution = Solution()
    word = ""
    expected_output = 1244858478
    print(f"Input: word = {word}")
    result = solution.numberOfSubstrings(word)
    print(f"Output: {result} \nExpected output: {expected_output}")